fun RenderContext.iconImage(svg: SvgIconSource, title: String="", baseClass: String?="w-30 h-30 m-2") {
    div("w-min") {
        if(title.isNotBlank()) {
            title(title)
        }
        svg(baseClass) {
            attr("viewBox",svg.viewBox)
            content(svg.content)
        }
    }
}